import { useParams, useNavigate } from "react-router-dom";
import axios from 'axios';
import { useEffect, useState } from 'react';

const Page3 = () => {
    const params = useParams();
    const [user, setUser] = useState(null);  // Изначально null, чтобы проверять на загруженность данных
    const [loading, setLoading] = useState(true); // Для отслеживания состояния загрузки
    const [error, setError] = useState(null); // Для хранения ошибок

    const navigate = useNavigate();

    useEffect(() => {
        setLoading(true);
        setError(null);

        axios(`https://jsonplaceholder.typicode.com/users/${params.id}`)
            .then(({ data }) => {
                setUser(data);
                setLoading(false);
            })
            .catch(err => {
                setError('Error fetching data');
                setLoading(false);
            });
    }, [params.id]);  // Зависимость от params.id

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div>
            {user ? (
                <>
                    <h1>{user.name}</h1>
                    <h1>{user.username}</h1>
                </>
            ) : (
                <div>User not found</div>
            )}
            <button onClick={() => navigate(-1)}>Go Back</button>
        </div>
    );
}

export default Page3;
